create table if not exists ${dbname}.core_Type( systemId int auto_increment primary key, versionId int);
create table if not exists ${dbname}.core_Attribute ( systemId int primary key, parentSystemId int, id varchar(255), value varchar(255), format varchar(255), unit varchar(255), rank int);
create table if not exists ${dbname}.core_configure_Component ( systemId int primary key, name varchar(255));
