create table if not exists ${dbname}.company ( systemId int primary key, companyNumber varchar(20));
create table if not exists ${dbname}.addressbook ( systemId int primary key, mePartyType char(3), mePartyId int);
create table if not exists ${dbname}.address ( systemId int primary key, line1 varchar(20), line2 varchar(20), line3 varchar(20), line4 varchar(20), line5 varchar(20), postcode varchar(10));
