target.sql:
-- Make sure the database and users are there before we try to delete them!
create database if not exists ${dbname};
grant all on ${dbname}.* to '${dbusername}'@'localhost' identified by '${dbpassword}' with grant option;
grant all on ${dbname}.* to '${dbusername}'@'localhost.localdomain' identified by '${dbpassword}' with grant option;

-- Now do the actual tidy up 
drop database if exists ${dbname};
revoke all privileges, grant option from '${dbusername}'@'localhost';
drop user '${dbusername}'@'localhost';

create database if not exists ${dbname}; grant all on ${dbname}.* to '${dbusername}'@'localhost' identified by '${dbpassword}' with grant option; grant all on ${dbname}.* to '${dbusername}'@'localhost.localdomain' identified by '${dbpassword}' with grant option;
create table if not exists ${dbname}.attribute ( id varchar(255) primary key, value varchar(255), format varchar(255), unit varchar(255), serial int );
create table if not exists ${dbname}.saved_policy_summary ( id bigint(20) auto_increment primary key, version bigint(20), quote_number varchar(255), policy_number varchar(255), quote_date date, expiry_date date, status varchar(255), page varchar(255), premium_amount decimal(19,2), premium_currency varchar(255), username varchar(255), product varchar(255), user_saved bit, test_case bit );
create table if not exists ${dbname}.saved_policy ( id bigint(20) primary key, policy text, quotation_document longblob, invoice_document longblob, wording_document longblob, certificate_document longblob );
